  `payment_method` varchar(30) NOT NULL DEFAULT '' COMMENT '支付方式',
  `trade_no` varchar(100) NOT NULL DEFAULT '' COMMENT '交易号',
  `add_time` int(11) NOT NULL DEFAULT '0' COMMENT '时间',
