  `is_postage_free` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否包邮，1：是包邮，0：不包邮',
  `postage_money` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '邮费',
