-- Table structure for `pigcms_order_friend_address`
-- ----------------------------
DROP TABLE IF EXISTS `pigcms_order_friend_address`;
CREATE TABLE `pigcms_order_friend_address` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT,
