-- ----------------------------
-- Table structure for `pigcms_order_sms`
-- ----------------------------
DROP TABLE IF EXISTS `pigcms_order_sms`;
CREATE TABLE `pigcms_order_sms` (
  `sms_order_id` int(11) NOT NULL AUTO_INCREMENT,
