  `price` double(10,2) DEFAULT '0.00' COMMENT '商品原价',
  `product_quantity` int(11) DEFAULT '0' COMMENT '商品原总库存(不同规格综合)',
  `product_count` int(11) DEFAULT '0' COMMENT '商品总库存',
  `privileged_cash` double(10,2) DEFAULT '0.00' COMMENT '特权_减多少现金',
