  `drp_deduct_point_sales` decimal(10,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '扣除分销商等级积分条件(销售额)',
  `drp_deduct_point` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '扣除分销商等级积分',
