ive">                        <p class="order-process-state">买家已下单</p>                        <p class="bar"><i class="square">√</i></p>                        <p class="order-process-time"><?php echo date('Y-m-d H:i:s', $order['add_time']); ?></p>                    </li>                    <li <?php if (in_array($order['status'], array(2, 3, 4, 7))) { ?>class="active"<?php } ?>>                        <p class="order-process-state"><?php if (in_array($order['status'], array(2, 3, 4, 7))) { ?>买家已付款<?php } else { ?>等待买家付款<?php } ?></p>                        <p class="bar"><i class="square">2</i></p>                        <?php if (in_array($order['status'], array(2, 3, 4, 7))) { ?>                        <p class="order-process-time"><?php echo date('Y-m-d H:i:s', $order['paid_time']); ?></p>                        <?php } ?>                    </li>                    <li <?php if (in_array($order['status'], array(3, 4, 7))) { ?>class="active"<?php } ?>>                        <p class="order-process-state">买家<?php echo $store_session['buyer_selffetch_name'] ? $store_session['buyer_selffetch_name'] : '上门自提' ?></p>                        <p class="bar"><i class="square">3</i></p>                        <?php                        if (in_array($order['status'], array(3, 4, 7))) {                        ?>                            <p class="order-process-time"><?php echo date('Y-m-d H:i:s', $order['delivery_time']); ?></p>                        <?php                        }                        ?>                    </li>                    <li <?php if ($order['status'] == 4) { ?>class="active"<?php } ?>>                        <p class="order-process-state">交易完成</p>                        <p class="bar"><i class="square">4</i></p>                        <?php if ($order['status'] == 4) { ?>                        <p class="order-process-time"><?php echo date('Y-m-d H:i:s', $order['complate_time']); ?></p>                        <?