<include file="Public:header"/>        <style type="text/css">            .c-gray {                color: #999;            }            .table-list tfoot tr {                height: 40px;            }            .green {                color: green;            }            .gray {                color: grey;            }            .red {                color:red;            }            .date-quick-pick {                display: inline-block;                color: #07d;                cursor: pointer;                padding: 2px 4px;                border: 1px solid transparent;                margin-left: 12px;                border-radius: 4px;                line-height: normal;            }            .date-quick-pick.current {                background: #fff;                border-color: #07d!important;            }            .date-quick-pick:hover{border-color:#ccc;text-decoration:none}            .statistics {                padding: 5px;            }            .statistics ul {                list-style: none;            }            .statistics ul li {                float: left;                width: 25%;                height: 50px;                padding: 10px 0;            }            .statistics ul li div {                margin-left: 10px;            }        </style>        <script type="text/javascript">            $(function(){                $('.status-enable > .cb-enable').click(function(){                    if (!$(this).hasClass('selected')) {                        if ($(this).closest('.status-enable').siblings('.status-disable').children('.cb-disable').hasClass('selected')) {                            window.top.msg(0, '此记录状态已修改，不要重复修改状态', true);                            return false;                        }                        var id = $(this).data('id');                        window.top.art.dialog({                            icon: 'question',                            title: '请确认',                            id: 'msg' + Math.random(),                            lock: true,                            fixed: true,                            opacity:'0.4',                            resize: false,                            content: '你确定这样操作吗？操作后可能不能恢复！',                            ok:function (){                                $.post("<?php echo U('Credit/chgReturnStatus'); ?>",{'status': 2, 'id': id}, function(data){                                    data = $.parseJSON(data);                                    window.top.msg(data.error, data.message, true);                                    if (data.error == 1) {                                        window.top.main_refresh();                                    }                                });                            },                            cancel:true                        });                        return false;                    }                })                $('.status-disable > .cb-disable').click(function(){                    if (!$(this).hasClass('selected')) {                        if ($(this).closest('.status-disable').siblings('.status-enable').children('.cb-enable').hasClass('selected')) {                            window.top.msg(0, '此记录状态已修改，不要重复修改状态', true);                            return false;                        }                        var id = $(this).data('id');                        window.top.art.dialog({                            icon: 'question',                            title: '请确认',                            id: 'msg' + Math.random(),                            lock: true,                            fixed: true,                            opacity:'0.4',                            resize: false,                            content: '你确定这样操作吗？操作后可能不能恢复！',                            ok:function (){                                $.post("<?php echo U('Credit/chgReturnStatus'); ?>", {'status': 3, 'id': id}, function (data) {                                    data = $.parseJSON(data);                                    window.top.msg(data.error, data.message, true);                                    if (data.error == 1) {                                        window.top.main_refresh();                                    }                                });                            },                            cancel:true                        });                        return false;                    }                })            })        </script>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('Credit/returnRecord')}" class="on" id="url_for_checkout" >提现记录</a>				</ul>			</div>			<table class="search_table" width="100%">				<tr>					<td>						<form action="{pigcms{:U('Credit/returnRecord')}" method="get">							<input type="hidden" name="c" value="Credit"/>							<input type="hidden" name="a" value="returnRecord"/>							筛选: <input type="text" name="keyword" class="input-text" value="{pigcms{$_GET['keyword']}" />							<select name="type">								<option value="order_no" <if condition="$_GET['type'] eq 'order_no'">selected="selected"</if>>单号</option>                                <option value="bank_account" <if condition="$_GET['type'] eq 'bank_account'">selected="selected"</if>>银行账户</option>								<option value="store" <if condition="$_GET['type'] eq 'store'">selected="selected"</if>>店铺名称</option>                                <option value="user" <if condition="$_GET['type'] eq 'user'">selected="selected"</if>>申请人</option>								<option value="tel" <if condition="$_GET['type'] eq 'tel'">selected="selected"</if>>联系电话</option>							</select>                            &nbsp;&nbsp;                            收款银行：                            <select name="bank">                                <option value="0">收款银行</option>                                <volist name="banks" id="bank">                                <option value="{pigcms{$bank.bank_id}" <if condition="$Think.get.bank eq $bank['bank_id']">selected</if>>{pigcms{$bank.name}</option>                                </volist>                            </select>                            &nbsp;&nbsp;处理状态：                            <select name="status">                                <option value="0">全部</option>                                <?php foreach ($status as $key => $value) { ?>                                <option value="<?php echo $key; ?>" <?php if ($_REQUEST['status'] == $key || ($key == 1 && $unprocessed > 0 && !isset($_GET['status']))) { ?>selected<?php } ?>><?php echo $value; ?></option>                                <?php } ?>                            </select>                            &nbsp;&nbsp;申请时间：                            <input type="text" name="start_time" id="js-start-time" class="input-text Wdate" style="width: 150px" value="{pigcms{$Think.get.start_time}" readonly />- <input type="text" name="end_time" id="js-end-time" style="width: 150px" class="input-text Wdate" value="{pigcms{$Think.get.end_time}" readonly />                            <span class="date-quick-pick" data-days="7">最近7天</span>                            <span class="date-quick-pick" data-days="30">最近30天</span>                            <input type="submit" value="查询" class="button"/>                            <input type="button" value="导出" class="button search_checkout" />						</form>					</td>				</tr>			</table>            <div class="table-list">                <table width="100%" cellspacing="0">                    <thead>                        <tr>                            <th>编号</th>                            <th>单号</th>                            <th>申请时间</th>                            <th>银行账户</th>                            <th>店铺名称</th>                            <th style="text-align: right">返还金额(元)</th>                            <th style="text-align: right">可用余额(元)</th>                            <th>申请人</th>                            <th>联系方式</th>                            <th>状态</th>                            <th>备注</th>                            <th class="textcenter">操作</th>                        </tr>                    </thead>                    <tbody>                        <if condition="is_array($platform_margin_logs)">                            <volist name="platform_margin_logs" id="platform_margin_log">                                <tr>                                    <td>{pigcms{$platform_margin_log.pigcms_id}</td>                                    <td><span class="c-gray">{pigcms{$platform_margin_log.order_no}</span></td>                                    <td>{pigcms{$platform_margin_log.add_time|date='Y-m-d H:i:s', ###}</td>                                    <td>                                        <span class="c-gray">账户类型：</span><if condition="$platform_margin_log['withdrawal_type'] eq 0">个人账户<else/>公司账户</if><br/>                                        <span class="c-gray">收款银行：</span><?php echo $platform_margin_log['bank']; ?><br />                                        <span class="c-gray">开户银行：</span><?php echo $platform_margin_log['opening_bank']; ?><br />                                        <span class="c-gray">银行帐户：</span><?php echo $platform_margin_log['bank_card']; ?><br />                                        <span class="c-gray">帐户名称：</span><?php echo $platform_margin_log['bank_card_user']; ?><br/><br/>                                    </td>                                    <td>{pigcms{$platform_margin_log.store}</td>                                    <td style="text-align: right;color:green;font-weight: bold;">{pigcms{$platform_margin_log.amount}</td>                                    <td style="text-align: right">{pigcms{$platform_margin_log.margin_balance}</td>                                    <td>{pigcms{$platform_margin_log.nickname}</td>                                    <td>{pigcms{$platform_margin_log.tel}</td>                                    <td><if condition="$platform_margin_log['status'] eq 1"><span class="red">待处理</span><elseif condition="$platform_margin_log['status'] eq 2"/><span class="green">已处理</span><elseif condition="$platform_margin_log['status'] eq 3"/><span class="gray">已取消</span></if></td>                                    <td>{pigcms{$platform_margin_log.bak}</td>                                    <td class="radio_box textcenter">                                        <div style="margin: 0 auto;width: 120px">                                            <span class="cb-enable status-enable"><label class="cb-enable<?php if ($platform_margin_log['status'] == 2) { ?> selected<?php } ?>" data-id="<?php echo $platform_margin_log['pigcms_id']; ?>"><span>已处理</span><input type="radio" name="status" value="2" <?php if ($platform_margin_log['status'] == 2) { ?> checked="checked"<?php } ?>></label></span>                                            <span class="cb-disable status-disable"><label class="cb-disable<?php if ($platform_margin_log['status'] == 3) { ?> selected<?php } ?>" data-id="<?php echo $platform_margin_log['pigcms_id']; ?>"><span>已取消</span><input type="radio" name="status" value="3" <?php if ($platform_margin_log['status'] == 3) { ?> checked="checked"<?php } ?>></label></span>                                            <div style="clear: both"></div>                                        </div>                                    </td>                                </tr>                            </volist>                        </if>                    </tbody>                    <tfoot>                        <if condition="is_array($platform_margin_logs)">                        <tr>                            <td class="pagebar" colspan="16">                                <div>                                    <div style="float: right">                                        {pigcms{$page}                                    </div>                                </div>                            </td>                        </tr>                        <else/>                        <tr><td class="textcenter red" colspan="16">列表为空！</td></tr>                        </if>                    </tfoot>                </table>            </div>		</div><include file="Public:checkout"/><include file="Public:footer"/>