<include file="Public:header"/><script type="text/javascript" src="static/js/layer/layer/layer.js"></script><script type="text/javascript">    $(function(){        var category_cat_num = '{pigcms{$now_category.cat_num}';        //是否启用        $('.status-enable > .cb-enable').click(function(){            if (!$(this).hasClass('selected')) {                cat_num = parseInt(category_cat_num);                now_len = parseInt($(".status-enable .selected").length);                if(cat_num<=now_len){                    layer.msg("该栏目下只能启用"+category_cat_num+"个推荐一元夺宝");return false;                }                var indiana_id = $(this).data('id');                $.post("{pigcms{:U('Indiana/activity_status')}",{'status': 1, 'id': indiana_id}, function(data){})            }        });        $('.status-disable > .cb-disable').click(function(){            if (!$(this).hasClass('selected')) {                var indiana_id = $(this).data('id');                if (!$(this).hasClass('selected')) {                    $.post("{pigcms{:U('Indiana/activity_status')}", {'status': 0, 'id': indiana_id}, function (data) {})                }            }        });    })</script><div class="mainbox">    <div id="nav" class="mainnav_title">        <ul>            <a href="{pigcms{:U('Indiana/home',array('fid'=>$now_category['fid']))}">返回分类列表</a>            <a href="{pigcms{:U('Indiana/indiana_list',array('cat_id'=>$now_category['cat_id']))}" class="on">{pigcms{$now_category.cat_name}-推荐列表</a>|            <a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Indiana/indiana_add',array('cat_id'=>$now_category['cat_id']))}','添加推荐一元夺宝活动',600,450,true,false,false,addbtn,'add');">{pigcms{$now_category.cat_name}-添加推荐</a>(最多启用{pigcms{$now_category.cat_num}个推荐活动)        </ul>    </div>    <form name="myform" id="myform" action="" method="post">        <div class="table-list">            <table width="100%" cellspacing="0">                <colgroup><col> <col> <col> <col><col> <col width="140" align="center"> </colgroup>                <thead>                <tr>                    <th>序号</th>                    <th>图片</th>                    <th>活动名称</th>                    <th>商品价</th>                    <th>状态</th>                    <th>是否启用</th>                    <th class="textcenter">操作</th>                </tr>                </thead>                <tbody>                <if condition="is_array($activity_home_lists)">                    <volist name="activity_home_lists" id="vo">                        <tr>                            <td>{pigcms{$vo.id}</td>                            <td><img src="{pigcms{$vo.image}" height="60"/></td>                            <td>{pigcms{$vo.activity_name}</td>                            <td>{pigcms{$vo.price}</td>                            <td>                                <if condition="$vo.activity_status eq 1">                                    进行中                                <elseif condition="$vo.activity_status eq 0"/>                                    未开始                                <else/>                                    已结束                                </if>                            </td>                            <td>                                <span class="cb-enable status-enable"><label class="cb-enable <if condition='$vo.status eq 1'>selected</if>" data-id="{pigcms{$vo.id}"><span>启用</span><input type="radio" name="status" value="1"></label></span>                                <span class="cb-disable status-disable"><label class="cb-disable <if condition='$vo.status eq 0'>selected</if>" data-id="{pigcms{$vo.id}"><span>关闭</span><input type="radio" name="status" value="0"></label></span>                            </td>                            <td class="textcenter">                                <a href="javascript:void(0);" class="delete_row" parameter="id={pigcms{$vo.id}" url="{pigcms{:U('Indiana/activity_del',array('id'=>$vo['id']))}">删除</a></td>                        </tr>                    </volist>                    <tr><td class="textcenter pagebar" colspan="9">{pigcms{$pagebar}</td></tr>                    <else/>                    <tr><td class="textcenter red" colspan="9">列表为空！</td></tr>                </if>                </tbody>            </table>        </div>    </form></div><include file="Public:footer"/>