<include file="Public:header"/><div class="mainbox">    <div id="nav" class="mainnav_title">        <ul>            <a href="{pigcms{:U('Indiana/home')}" class="on">一元夺宝首页分类列表</a>            <if condition="$activity_category_detail.cat_key eq 'wap_indiana_define'">            <a href="javsscript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Indiana/indianacat_add',array('cat_id'=>$activity_category_detail['cat_id']))}','新增模块',500,200,true,false,false,addbtn,'add',true);">新建内容模块</a>            </if>        </ul>    </div>    <form name="myform" id="myform" action="" method="post">        <div class="table-list">            <table width="100%" cellspacing="0">                <thead>                <tr>                    <th>编号</th>                    <th>名称</th>                    <th>标识</th>                    <th>夺宝活动推荐列表</th>                    <th>操作</th>                </tr>                </thead>                <tbody>                <if condition="is_array($activity_category_list)">                    <volist name="activity_category_list" id="vo">                        <tr>                            <td>{pigcms{$vo.cat_id}</td>                            <td>                                <if condition="$vo['show'] eq 1">                                    <a href="{pigcms{:U('Indiana/home',array('fid'=>$vo['cat_id']))}">{pigcms{$vo.cat_name}</a>                                <else/>                                    <span>{pigcms{$vo.cat_name}</span>                                </if>                            </td>                            <td>{pigcms{$vo.cat_key}</td>                            <td>                                <if condition="$vo['show'] neq 1">                                    <a href="{pigcms{:U('Indiana/indiana_list',array('cat_id'=>$vo['cat_id']))}">推荐夺宝列表</a>                                <else/>                                    -                                </if>                            </td>                            <td>                                <if condition="$vo['show'] neq 1">                                <a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Indiana/indianacat_edit',array('cat_id'=>$vo['cat_id']))}','编辑夺宝分类名',500,200,true,false,false,editbtn,'add',true);">编辑</a>                                <else/>                                -                                </if>                            </td>                        </tr>                    </volist>                    <else/>                    <tr><td class="textcenter red" colspan="8">列表为空！</td></tr>                </if>                </tbody>            </table>        </div>    </form></div><include file="Public:footer"/>