<include file="Public:header"/>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('News/index')}" class="on">新闻分类列表</a>|					<a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('News/catAdd')}','添加新闻分类',600,400,true,false,false,addbtn,'add',true);">添加新闻分类</a>				</ul>			</div>			<form name="myform" id="myform" action="" method="post">				<div class="table-list">					<table width="100%" cellspacing="0">						<colgroup><col> <col> <col><col>  <col width="180" align="center"> </colgroup>						<thead>							<tr>								<th>编号</th>								<th>名称</th>								<th>备注</th>								<th>标识</th>								<th>是否显示</th>								<th>排序</th>								<th class="textcenter">操作</th>							</tr>						</thead>						<tbody>							<if condition="is_array($category_list)">								<volist name="category_list" id="vo">									<tr>										<td>{pigcms{$vo.cat_id}</td>										<td>{pigcms{$vo.cat_name}</td>										<td>{pigcms{$vo.desc}</td>										<td>{pigcms{$vo.cat_key}</td>										<td><if condition="$vo['cat_state'] eq 1">显示<else />隐藏</if></td>										<td>{pigcms{$vo.sort}</td>										<td class="textcenter">                                                                                    <a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('News/catShow',array('cat_id'=>$vo['cat_id'],'frame_show'=>true))}','查看新闻分类',600,400,true,false,false,false,'add',true);">查看</a> |                                                                                    <a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('News/catEdit',array('cat_id'=>$vo['cat_id']))}','编辑新闻分类',600,400,true,false,false,editbtn,'add',true);">编辑</a>                                                                                </td>									</tr>								</volist>								<tr><td class="textcenter pagebar" colspan="7">{pigcms{$pagebar}</td></tr>							<else/>								<tr><td class="textcenter red" colspan="7">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div><include file="Public:footer"/>