<include file="Public:header"/>    <style type="text/css">		.box > div {			margin-left: 10px;		}		.box > div > .label {			font-weight: bold;		}		.box > div > .money {			display: inline-block;			margin-top: 10px;		}		.statistics-details ul li {			padding: 5px;			line-height: 15px;		}		.red {			color:red;		}    </style>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('Order/incomeRecord')}" class="on">平台收益</a> <span class="red">收益数据，没有涉及到第三方支付手续费数据（需以实际收款数据为准）。</span>				</ul>			</div>			<form name="myform" id="myform" action="" method="post">				<div class="table-list" style="clear: both">					<table width="100%" cellspacing="0">						<thead>							<tr>								<th>收益来源</th>								<th>付款店铺</th>								<th style="text-align: right">收益金额(元)</th>								<th style="text-align: center">添加时间</th>								<th>备注</th>							</tr>						</thead>						<tbody>							<if condition="is_array($incomes)">								<volist name="incomes" id="income">									<tr>										<td><if condition="$income['type'] eq 1">平台服务费<elseif condition="$income['type'] eq 2"/>店铺认证费</if></td>										<td>{pigcms{$income['store']}</td>										<td style="text-align: right;color:green;font-weight: bold;">+{pigcms{$income['income']}</td>										<td style="text-align: center">{pigcms{$income['add_time']|date='Y-m-d H:i:s', ###}</td>										<td>{pigcms{$income['bak']|default='无'}</td>									</tr>								</volist>                                <tr>                                    <td class="textcenter pagebar" colspan="5">{pigcms{$page}</td>                                </tr>							<else/>								<tr><td class="textcenter red" colspan="5">列表为空！</td></tr>							</if>						</tbody>					</table>				</div>			</form>		</div><include file="Public:footer"/>