section-title > h2 {		font-size: 14px;	}	.section-express .section-title h2, .section-express .section-title ul, .section-express .section-title li {		float: left;		height: 34px;		line-height: 34px;		min-width: 124px;	}	.section-express .section-title h2 {		font-weight: bold;	}	.section-express .section-title h2, .section-express .section-title ul, .section-express .section-title li {		float: left;		height: 34px;		line-height: 34px;		min-width: 124px;	}	.section-express .section-title li {		text-align: center;		border-left: 1px solid #e5e5e5;		border-right: 1px solid #e5e5e5;		margin-left: -1px;		cursor: pointer;	}	.section-express .section-title li.active {		background: #fff;	}	.section-express .section-detail {		display: block;	}	.order-goods .tb-name {		width: 220px;	}	.order-goods .tb-price {		width: 80px;	}	.order-goods .tb-num {		width: 80px;	}	.order-goods .tb-total {		width: 90px;	}	.order-goods .tb-state {		width: 80px;	}	.order-goods tr>.tb-coupon, .order-goods tr>.tb-postage {		width: 108px;		text-align: center;		padding-left: 0;	}	.order-goods tr>.tb-postage {		vertical-align: middle;	}	a {		color: #07d;		text-decoration: none;	}	a.new-window {		color: #00f;	}	.order-goods .msg-row td {		border-top: 1px dashed #EEE;	}	.order-goods th, .order-goods td {		-webkit-box-sizing: border-box;		-moz-box-sizing: border-box;		box-sizing: border-box;	}	.order-goods tr>th, .order-goods tr>td {		padding: 5px 0 5px 5px;		word-break: break-all;	}	.order-goods .msg-row td {		border-top: 1px dashed #EEE;	}	.order-goods tbody>tr {		border-bottom: 0 none;	}	.order-goods tbody .tb-state, .order-goods tbody .tb-coupon {		border-right: 1px solid #E4E4E4;	}	p {		margin: 0 0 10px;	}	.order-process li:last-child .bar:after {		margin-left: -94px;	}	.hide {		display: none;	}	.button {		background: none;		border: none;		display: inline-block;		padding: 4px 11px;		margin-bottom: 0;		font-size: 12px;		line-height: 20px;		color: #fff;		text-align: center;		vertical-align: middle;		cursor: pointer;		-webkit-border-radius: 2px;		-moz-border-radius: 2px;		border-radius: 2px;		background: #009adb;		text-shadow: none;}</style><link rel="stylesheet" type="text/css" href="{pigcms{$static_public}css/fancybox.css" /><script type="text/javascript" src="{pigcms{$static_public}js/jquery.fancybox-1.3.1.pack.js"></script><script type="text/javascript" src="{pigcms{$static_public}js/layer/layer.min.js"></script><script type="text/javascript">$(function(){	try {		$("a[rel=show_img]").fancybox({			'titlePosition' : 'over',			'cyclic'		: false,		});	} catch (e) {			}	$('.refund-btn').click(function() {		var return_id = $(this).data('id');		$.layer({			shade: [0],			area: ['auto','auto'],			dialog: {				msg: '确定给客户退款？',				btns: 2,				type: 4,				btn: ['确定','取消'],				yes: function(){					$.post("{pigcms{:U('Order/refund_process')}", {'id': return_id, 'act': 'refund'}, function(data) {						if (data.status) {							layer.alert(data.message, 1, function(){								window.top.frames["main"].location.reload();								window.top.art.dialog.list['process'].close();							});						} else {							layer.alert(data.message, 3);						}					});				}, no: function(){				}			}		});	});	$('.cancel-btn').click(function() {		var return_id = $(this).data('id');		$.layer({			shade: [0],			area: ['auto','auto'],			dialog: {				msg: '确定取消退款处理？',				btns: 2,				type: 4,				btn: ['确定','取消'],				yes: function(){					$.post("{pigcms{:U('Order/refund_process')}", {'id': return_id, 'act': 'cancel'}, function(data) {						if (data.status) {							layer.alert(data.message, 1, function(){								window.top.frames["main"].location.reload();								window.top.art.dialog.list['process'].close();							});						} else {						 	layer.alert(data.message, 3);						}					});				}, no: function(){				}			}		});	});})</script><div class="section">	<h2 class="section-title clearfix">		退货详情	</h2>	<div class="section-detail clearfix">		<table>			<tbody>				<tr>					<td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;状态：</td>					<td><?php echo $return['status_txt'] ?></td>				</tr>				<tr>					<td>下单用户：</td>					<td><?php echo $return['nickname'] ?> <?php echo $return['phone'] ?></td>				</tr>				<tr>					<td>支付方式：</td>					<td><?php echo $order['payment_method']; ?></td>				</tr>				<tr>					<td>交易单号：</td>					<td><?php echo $order['third_id']; ?></td>				</tr>				<tr>					<td>支付金额：</td>					<td><span style="font-weight: bold;color: red;"><?php echo $order['pay_money']; ?></span> 元 (含运费 <?php echo $order['postage']; ?> 元)</td>				</tr>				<tr>					<td>剩余可退：</td>					<td><span style="font-weight: bold;color: green;"><?php echo $order['pay_money_balance']; ?> 元</td>				</tr>				<tr>					<td>退货单号：</td>					<td><?php echo $return['return_no']; ?></td>				</tr>				<tr>					<td>维权类型：</td>					<td><?php echo $return['type_txt'] ?></td>				</tr>				<tr>					<td>维权理由：</td>					<td><?php echo nl2br(htmlspecialchars($return['content'])) ?></td>				</tr>				<tr>					<td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;图片：</td>					<td>						<?php 						if (is_array($return['images'])) {							foreach ($return['images'] as $tmp) {						?>								<a href="<?php echo $tmp ?>" rel="show_img">									<img src="<?php echo $tmp ?>" style="max-width:60px; max-height:60px; float:left; padding-right:5px;">								</a>						<?php							}						} else {						?>							暂无图片						<?php						}						?>					</td>				</tr>			</tbody>		</table>	</div>	<h2 class="section-title clearfix">		审核信息	</h2>	<div class="section-detail clearfix">		<table>			<tbody>				<?php 					if ($return['cancel_dateline']) {					?>						<tr>							<td>不同意说明</td>							<td><?php echo htmlspecialchars($return['store_content']) ?></td>						</tr>					<?php					}					if (!empty($return['is_delivered'])) {						$address = unserialize($return['address']);					?>						<tr>							<td>收货信息</td>							<td><?php echo htmlspecialchars($return['address_user']) ?> <?php echo $return['address_tel'] ?></td>						</tr>						<tr>							<td>收货地址</td>							<td><?php echo $address['province_txt'] . $address['city_txt'] . $address['area_txt'] . $address['address'] ?></td>						</tr>						<tr>							<td>退货金额</td>							<td><span style="color:green;font-weight: bold;"><?php echo sprintf('%.2f', $return['product_money'] + $return['postage_money']) ?></span> 元 = 退货产品金额：<?php echo $return['product_money'] ?> 元 + 退货物流金额：<?php echo $return['postage_money'] ?> 元</td>							<td align="right">								<?php if ($order['pay_money_balance'] >= $return['total']) { ?><input type="button" class="button refund-btn" data-id="<?php echo $return['id']; ?>" value="确认退款" /><?php } else { ?><span style="color: red;">订单支付金额剩余不足，无法退款。</span><?php } ?>								<input type="button" class="button cancel-btn" data-id="<?php echo $return['id']; ?>" value="取消退款" />							</td>						</tr>					<?php 					} else {					?>						<tr>							<td>退货金额</td>							<td><span style="color:green;font-weight: bold;"><?php echo sprintf('%.2f', $return['product_money'] + $return['postage_money']) ?></span> 元 = 退货产品金额：<?php echo $return['product_money'] ?> 元 + 退货物流金额：<?php echo $return['postage_money'] ?> 元</td>							<td align="right">								<?php if ($order['pay_money_balance'] >= $return['total']) { ?><input type="button" class="button refund-btn" data-id="<?php echo $return['id']; ?>" value="确认退款" /><?php } else { ?><span style="color: red;">订单支付金额剩余不足，无法退款。</span><?php } ?>								<input type="button" class="button cancel-btn" data-id="<?php echo $return['id']; ?>" value="取消退款" />							</td>						</tr>					<?php					}					?>			</tbody>		</table>	</div></div><div class="section">	<h2 class="section-title clearfix">		分润明细	</h2>	<div class="section-detail clearfix">		<table class="table order-goods">			<thead>				<tr>					<td></td>					<td>店铺名称</td>					<td>联系人</td>					<td>联系电话</td>					<td>利润 (元)</td>				</tr>			</thead>			<?php 			foreach ($store_list as $store) {			?>				<tr>					<td><img src="<?php echo $store['logo'] ?>" style="max-width:60px; max-height:60px;" /></td>					<td>						<?php echo htmlspecialchars($store['name']) ?>						<?php 						if ($store['is_fx'] == 1) {						?>							<br /><span style="background:#f60; padding:2px 7px; color:white; border-radius:2px;"><?php echo $store['drp_level'] ?>级分销商</span>						<?php						} else if ($store['is_fx'] == 2) {						?>							<br /><span style="background-color:#07d; padding:2px 7px; color:white; border-radius:2px;">经销商</span>						<?php 						} else {						?>							<br /><span style="background:#f60; padding:2px 7px; color:white; border-radius:2px;">供货商</span>						<?php						}						?>					</td>					<td><?php echo htmlspecialchars($store['linkman']) ?></td>					<td><?php echo $store['service_tel'] ?></td>					<td><?php echo sprintf('%.2f', $store['profit']) ?></td>				</tr>			<?php			}			?>		</table>	</div></div><include file="Public:footer"/>