<include file="Public:header"/>        <style type="text/css">            .c-gray {                color: #999;            }            .table-list tfoot tr {                height: 40px;            }            .green {                color: green;            }            a, a:hover{                text-decoration: none;            }        </style>        <script type="text/javascript">            $(function() {                //是否启用                $('.status-enable > .cb-enable').click(function(){                    if (!$(this).hasClass('selected')) {                        var tag_id = $(this).data('id');                        $.post("<?php echo U('Store/tag_status'); ?>",{'status': 1, 'tag_id': tag_id}, function(data){})                    }                });                $('.status-disable > .cb-disable').click(function(){                    if (!$(this).hasClass('selected')) {                        var tag_id = $(this).data('id');                        if (!$(this).hasClass('selected')) {                            $.post("<?php echo U('Store/tag_status'); ?>", {'status': 0, 'tag_id': tag_id}, function (data) {})                        }                    }                });            })        </script>		<div class="mainbox">			<div id="nav" class="mainnav_title">				<ul>					<a href="{pigcms{:U('Store/tag')}" class="on">类别列表</a>|                    <a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Store/tag_add')}','添加类别',480,150,true,false,false,addbtn,'add',true);">添加类别</a>				</ul>			</div>			            <div class="table-list">                <table width="100%" cellspacing="0">                    <thead>                        <tr>                            <th>删除 | 修改</th>                            <th>编号</th>                            <th>名称</th>                            <th>状态</th>                            <th>排序</th>                            <th class="textcenter" width="100">操作</th>                        </tr>                    </thead>                    <tbody>                        <if condition="is_array($tags)">                            <volist name="tags" id="tag">                                <tr>                                    <td><a url="<?php echo U('Store/tag_del', array('id' => $tag['tag_id'])); ?>"  class="delete_row"><img src="{pigcms{$static_path}images/icon_delete.png" width="18" title="删除" alt="删除" /></a> | <a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Store/tag_edit', array('id' => $tag['tag_id']))}','修改类别 - {pigcms{$tag.name}',480,200,true,false,false,editbtn,'edit',true);"><img src="{pigcms{$static_path}images/icon_edit.png" width="18" title="修改" alt="修改" /></a></td>                                    <td>{pigcms{$tag.tag_id}</td>                                    <td><span style="font-weight: bold;" >{pigcms{$tag.name}</span></td>                                                                        <td>                                        <if condition="$tag['status'] eq 1"><span class="green">启用</span><else/><span class="red">禁用</span></if>                                    </td>                                    <td>{pigcms{$tag.order_by}</td>                                    <td>                                    <span class="cb-enable status-enable"><label class="cb-enable <if condition="$tag['status'] eq 1">selected</if>" data-id="<?php echo $tag['tag_id']; ?>"><span>启用</span><input type="radio" name="status" value="1" <if condition="$tag['status'] eq 1">checked="checked"</if> /></label></span>                                    <span class="cb-disable status-disable"><label class="cb-disable <if condition="$tag['status'] eq 0">selected</if>" data-id="<?php echo $tag['tag_id']; ?>"><span>禁用</span><input type="radio" name="status" value="0" <if condition="$tag['status'] eq 0">checked="checked"</if>/></label></span>                                    </td>                                </tr>                            </volist>                        </if>                    </tbody>                    <tfoot>                        <if condition="is_array($tags)">                        <tr>                            <td class="textcenter pagebar" colspan="7">{pigcms{$page}</td>                        </tr>                        <else/>                        <tr><td class="textcenter red" colspan="7">列表为空！</td></tr>                        </if>                    </tfoot>                </table>            </div>		</div><include file="Public:footer"/>