<include file="Public:header"/><div class="mainbox">    <div id="nav" class="mainnav_title">        <ul>            <a href="{pigcms{:U('Tuan/home')}" class="on">团购首页分类列表</a>        </ul>    </div>    <form name="myform" id="myform" action="" method="post">        <div class="table-list">            <table width="100%" cellspacing="0">                <thead>                <tr>                    <th>编号</th>                    <th>名称</th>                    <th>标识</th>                    <th>团购活动推荐列表</th>                    <th>操作</th>                </tr>                </thead>                <tbody>                <if condition="is_array($tuan_category_list)">                    <volist name="tuan_category_list" id="vo">                        <tr>                            <td>{pigcms{$vo.cat_id}</td>                            <td>                                <if condition="$vo['show'] eq 1">                                    <a href="{pigcms{:U('Tuan/home',array('fid'=>$vo['cat_id']))}">{pigcms{$vo.cat_name}</a>                                <else/>                                    <span>{pigcms{$vo.cat_name}</span>                                </if>                                <if condition="$vo['cat_key'] eq 'pc_tuan_home'">                                (url地址:<?php echo $config['site_url'];?>/index.php?c=tuan)                                <elseif condition="$vo['cat_key'] eq 'wap_tuan_home'"/>                                (url地址:<?php echo $config['site_url'];?>/webapp/groupbuy/#/channel)                                </if>                            </td>                            <td>{pigcms{$vo.cat_key}</td>                            <td>                                <if condition="$vo['show'] neq 1">                                    <a href="{pigcms{:U('Tuan/tuan_list',array('cat_id'=>$vo['cat_id']))}">推荐团购列表</a>                                <else/>                                    -                                </if>                            </td>                            <td>                                <a href="javascript:void(0);" onclick="window.top.artiframe('{pigcms{:U('Tuan/tuancat_edit',array('cat_id'=>$vo['cat_id']))}','编辑团购分类名',500,200,true,false,false,editbtn,'add',true);">编辑</a>                            </td>                        </tr>                    </volist>                    <else/>                    <tr><td class="textcenter red" colspan="8">列表为空！</td></tr>                </if>                </tbody>            </table>        </div>    </form></div><include file="Public:footer"/>