<script>
$(function(){
	if($('.js-regions-wrap').data('province') == ''){
		getProvinces('s1','');
	}else{
		getProvinces('s1',$('.js-regions-wrap').data('province'));
		getCitys('s2','s1',$('.js-regions-wrap').data('city'));
		getAreas('s3','s2',$('.js-regions-wrap').data('county'));
	}

	$('#s1').live('change',function(){
		if($(".js-regions-wrap").find(".buttons").size()) {
			$(".js-regions-wrap .buttons").empty().detach();;
		}		
		if($(this).val() != ''){
			getCitys('s2','s1','');
		}else{
			$('#s2').html('<option value="">选择城市</option>');
		}
		$('#s3').html('<option value="">选择地区</option>');
	});
	$('#s2').live('change',function(){
		if($(this).val() != ''){
			getAreas('s3','s2','');

			if($(".js-regions-wrap").find(".buttons").size()) {
				$(".js-regions-wrap .buttons").empty().detach();;
			}
			var button =' <button type="button" name="button" class="buttons ng-binding">确定</button>';

			$(".js-regions-wrap").append(button);
		}else{
			if($(".js-regions-wrap").find(".buttons").size()) {
				$(".js-regions-wrap .buttons").empty().detach();;
			}
			$('#s3').html('<option value="">选择地区</option>');
		}
	});


		
})

</script>





